    public async Task ConnectLobby()
    {
        try
        {
            string baseUrl = navigationManager.BaseUri;
            _hubUrl = baseUrl.TrimEnd('/') + LobbyHub.HubUrl;

            _hubConnection = new HubConnectionBuilder()
                    .WithUrl(_hubUrl)
                    .Build();

            _hubConnection.On<string, string, string>("Broadcast", AddNewLobby);

            await _hubConnection.StartAsync();
        }
        catch (Exception e)
        {
            Snackbar.Add($"ERROR: Failed to start chat client: {e.Message}", Severity.Error);
        }
    }

    private async Task DisconnectAsync()
    {
        await _hubConnection.StopAsync();
        await _hubConnection.DisposeAsync();
        _hubConnection = null;
    }

